import React, { useState, useRef } from 'react';
import { Camera } from 'react-camera-pro';  

const WebcamCapture = ({ isWebcamOpen, captureImageFromWebcam }) => {
  const cameraRef = useRef(null);  

  const capturePhoto = () => {
    if (cameraRef.current) {
      const imageSrc = cameraRef.current.takePhoto();
      captureImageFromWebcam(imageSrc);
    }
  };

  return (
    <div className="mb-4">
      {isWebcamOpen && (
        <>
          <Camera
            ref={cameraRef}  // Pass the ref correctly
            onError={(error) => console.error('Webcam Error: ', error)}
            onInitialized={() => console.log('Camera Initialized')}
            facingMode="environment"
            imageWidth="640"
            imageHeight="480"
          />
          <button
            onClick={capturePhoto}
            className="mt-4 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-700"
          >
            Capture Photo
          </button>
        </>
      )}
    </div>
  );
};

export default WebcamCapture;
