import React, { useRef, useState, useEffect } from 'react';
import { Camera } from 'react-camera-pro';

const WebcamCapture = ({ isWebcamOpen, captureImageFromWebcam }) => {
  const cameraRef = useRef(null);
  const [isCapturing, setIsCapturing] = useState(false);
  const [cameraReady, setCameraReady] = useState(false);

  // Monitor if the camera is ready
  useEffect(() => {
    if (cameraRef.current) {
      console.log("Camera is ready to capture.");
      setCameraReady(true);
    } else {
      setCameraReady(false);
    }
  }, [cameraRef.current]);

  useEffect(() => {
    if (isWebcamOpen) {
      // Open webcam or initialize camera
      console.log("Webcam opened");
    } else {
      // Clean up webcam when closed
      console.log("Webcam closed");
      setCameraReady(false); // Reset camera readiness when webcam is closed
    }
  }, [isWebcamOpen]);

  const capture = () => {
    if (cameraReady && cameraRef.current) {
      const imageSrc = cameraRef.current.takePhoto(); // `takePhoto()` is how we capture the image in `react-camera-pro`
      captureImageFromWebcam(imageSrc);
    } else {
      console.error("Camera is not ready.");
    }
  };

  return (
    <div>
      {isWebcamOpen && (
        <div>
          {/* React Camera Pro component */}
          <Camera
            ref={cameraRef}
            onTakePhoto={(dataUri) => captureImageFromWebcam(dataUri)}
            style={{ width: '100%', height: 'auto' }}
          />
          <button onClick={capture} className="mt-4">
            Capture Photo
          </button>
        </div>
      )}
    </div>
  );
};

export default WebcamCapture;
