import React, { useRef, useState, useEffect } from 'react';

const WebcamCapture = ({ isWebcamOpen, captureImageFromWebcam }) => {
  const webcamRef = useRef(null);
  const [isCapturing, setIsCapturing] = useState(false);

  useEffect(() => {
    if (isWebcamOpen) {
      // Open webcam or initialize the camera
      console.log("Webcam opened");
    } else {
      // Clean up webcam when closed
      console.log("Webcam closed");
    }
  }, [isWebcamOpen]);

  const capture = () => {
    if (webcamRef.current) {
      const imageSrc = webcamRef.current.getScreenshot();
      captureImageFromWebcam(imageSrc);
    }
  };

  return (
    <div>
      {isWebcamOpen && (
        <div>
          {/* Assuming you are using a webcam library like react-webcam */}
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            width="100%"
          />
          <button onClick={capture} className="mt-4">
            Capture Photo
          </button>
        </div>
      )}
    </div>
  );
};

export default WebcamCapture;
