import React, { useState, useRef, useEffect } from 'react';
import Webcam from 'react-webcam';

// Product Recommendations Component
const ProductRecommendations = ({ seasonTag }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchRecommendations = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://127.0.0.1:5000/get_recommendations', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ season: seasonTag }),
      });

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setProducts(data.recommended_products || []);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      setError(error.message || 'An error occurred.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (seasonTag) fetchRecommendations();
  }, [seasonTag]);

  return (
    <div className="mt-8 flex flex-col items-center">
      <h2 className="text-2xl font-bold mb-4">Recommended Products for {seasonTag}</h2>
      {loading && <p>Loading recommendations...</p>}
      {error && <p className="text-red-500">{error}</p>}
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 w-full max-w-5xl">
        {products.length > 0 ? (
          products.map((product, index) => (
            <div key={index} className="border p-4 rounded-lg shadow-lg w-60">
              <h3 className="font-bold text-lg">{product.title}</h3>
              {product.image_url && (
                <img
                  src={product.image_url}
                  alt={product.title}
                  className="w-full h-40 object-cover mb-4"
                />
              )}
              <a href={product.product_url} target="_blank" rel="noopener noreferrer">
                View Product
              </a>
            </div>
          ))
        ) : (
          <p>No products found for this season.</p>
        )}
      </div>
    </div>
  );
};

const Tool = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showRecommendations, setShowRecommendations] = useState(false);
  const [isWebcamOpen, setIsWebcamOpen] = useState(false);
  const webcamRef = useRef(null);

  const captureImageFromWebcam = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    const blob = dataURItoBlob(imageSrc);
    const file = new File([blob], "webcam_photo.jpg", { type: "image/jpeg" });
    setSelectedImage(file);
    setIsWebcamOpen(false);
  };

  const dataURItoBlob = (dataURI) => {
    const byteString = atob(dataURI.split(',')[1]);
    const mimeString = dataURI.split(',')[0].split(':')[1].split(';')[0];
    const buffer = new Uint8Array(byteString.length);
    for (let i = 0; i < byteString.length; i++) {
      buffer[i] = byteString.charCodeAt(i);
    }
    return new Blob([buffer], { type: mimeString });
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) setSelectedImage(file);
    setResult(null);
    setShowRecommendations(false);
  };

  const analyzeColor = async () => {
    if (!selectedImage) return;
    setLoading(true);
    const formData = new FormData();
    formData.append('file', selectedImage);

    try {
      const response = await fetch('http://127.0.0.1:5000/predict', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) throw new Error('Error analyzing image');
      const data = await response.json();
      setResult(data.predicted_season);
    } catch (error) {
      console.error('Error analyzing image:', error);
      setResult('An error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white p-8 flex flex-col items-center">
      <h1 className="text-4xl font-extrabold mb-8">Our Colour Analysis Tool</h1>
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-3xl">
        <button
          onClick={() => setIsWebcamOpen((prev) => !prev)}
          className="mb-4 px-4 py-2 bg-indigo-500 text-white rounded-lg"
        >
          {isWebcamOpen ? 'Close Webcam' : 'Open Webcam'}
        </button>
        {isWebcamOpen && (
          <div className="mb-4">
            <Webcam audio={false} ref={webcamRef} screenshotFormat="image/jpeg" />
            <button
              onClick={captureImageFromWebcam}
              className="mt-4 px-4 py-2 bg-green-500 text-white rounded-lg"
            >
              Capture Photo
            </button>
          </div>
        )}
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        <button onClick={analyzeColor} disabled={!selectedImage || loading}>
          {loading ? 'Analyzing...' : 'Analyze Colour'}
        </button>
        {result && <p>Your Predicted Season: {result}</p>}
        {showRecommendations && <ProductRecommendations seasonTag={result} />}
      </div>
    </div>
  );
};

export default Tool;
